pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

contract Diploma{
    uint public reportCount = 0;

    struct DiplomaObj{
      uint id;
      string diplomaHash;
      string diplomaName;
      string diplomaStudenID;
    }

    mapping(uint => DiplomaObj) public diplomas;

    function createDiploma(string memory _diplomaHash, string memory _diplomaName, string memory _diplomaStudenID) public{
        diplomas[reportCount] = DiplomaObj(reportCount, _diplomaHash, _diplomaName,_diplomaStudenID);
        reportCount ++;


    }

    function getDiplomaCount() view public returns (uint) {
      return reportCount;
    }

    function getTdip() public view returns (DiplomaObj[] memory){
      DiplomaObj[] memory trrips = new DiplomaObj[](reportCount);
      for (uint i = 0; i < reportCount; i++) {
          DiplomaObj storage trrip = diplomas[i];
          trrips[i] = trrip;
      }
      return trrips;
  }
}
